#!/bin/zsh
# setting up zsh options
# https://zsh.sourceforge.io/Doc/Release/Options.html

# Changing directories
CHANGING_DIRECTORIES=(
	AUTO_CD
	CDABLE_VARS
	PUSHD_IGNORE_DUPS
)

COMPLETION=(
	ALWAYS_LAST_PROMPT
	ALWAYS_TO_END
	AUTO_NAME_DIRS
	COMPLETE_IN_WORD
	LIST_ROWS_FIRST
)

HISTORY=(
	HIST_FIND_NO_DUPS
	HIST_IGNORE_ALL_DUPS
	HIST_IGNORE_DUPS
	HIST_SAVE_NO_DUPS
	HIST_VERIFY
	SHARE_HISTORY
)

INPUT_OUTPUT=(
	PRINT_EXIT_VALUE
)

PROMPTING=(
	PROMPT_SUBST
)

SHELL_STATE=(
	INTERACTIVE
	LOGIN
)

setopt $CHANGING_DIRECTORIES $COMPLETION $HISTORY $INPUT_OUTPUT $PROMPTING $SHELL_STATE

HISTSIZE=10000
SAVEHIST=10000
